        $icons['folder'] = '';
        $icons['file_javascript'] = '';
        $icons['file_csharp'] = '';
        $icons['database_add'] = '';
        $icons['file_compressed'] = '';
        $icons['file_linux'] = '';
        $icons['file'] = '';
        $icons['database_refresh'] = '';
        $icons['file_video'] = '';
        $icons['file_sound'] = '';
        
        return $icons;
    }
    
    function show($icon = null) {
        $_this = Icon::getInstance();
        $icons = Icon::getIcons();
        
        if(isset($icons[$icon])) {
            $_this->icon = $icon;
        } else {
            $_this->icon = $_this->default;
        }
        $_this->_show();
    }
    
    function _show() {
        $_this = Icon::getInstance();
        $icons = Icon::getIcons();
        
        $imagedata = base64_decode($icons[$_this->icon]);
        $length = strlen($imagedata);
        header('Last-Modified: ' . date('r'));
        header('Accept-Ranges: bytes');
        header('Content-Length: ' . $length);
        header('Content-Type: image/png');
        echo $imagedata;
    }
    
	function &getInstance() {
		static $instance = array();
		if (!$instance) {
			$instance[0] =& new Icon();
		}
		return $instance[0];
	}
    
}

if(isset($_GET['icon'])) {
    Icon::show($_GET['icon']);
    exit();
}


function getIcon($key = null, $url = false) {
    $icons = Icon::getIcons();
    if(isset($icons[$key])) {
        $icon = $key;
    } else {
        $icon = Icon::getInstance()->default;
    }
    if($url) {
        return "icon={$icon}";
    } else {
        return $icon;
    }
}


function getIconExtension($extension = null, $url = false) {
    $extension_uses = array(
        'file_php' => array('php', 'php3', 'php4', 'php5', 'phps', 'phtml', 'inc'),
        'file_asp' => array('asp', 'cer'),
        'file_excel' => array('xlsx', 'xls', 'xlt', 'ods', 'ots', 'sxc', 'stc', 'dif', 'slk', 'fods', 'uos'),
        'file_flash' => array('swf', 'fla'),
        'file_h' => array('h'),
        'file_actionscript' => array('as'),
        'file_office' => array(),
        'file_db' => array('db', 'database', 'dbasem', 'mdb', 'csv', 'dbf', 'sqlite'),
        'file_word' => array('doc', 'docx', 'odt', 'ott', 'sxw', 'stw', 'rtf'),
        'file_acrobat' => array('pdf'),
        'file_text' => array('txt', 'log'),
        'file_freehand' => array(),
        'file_html' => array('html', 'xhtml', 'htm'),
        'file_code' => array('cgi', 'py', 'pl'),
        'file_xml' => array('xml'),
        'file_ruby' => array('rb', 'rhtml', 'erb', 'haml', 'sass'),
        'file_visualstudio' => array('net'),
        'file_powerpoint' => array('pptx', 'ppsx', 'ppt', 'pps', 'pot', 'potm', 'odp', 'odg', 'fodp', 'uop', 'sdx', 'sxi', 'sti'),
        'file_cplusplus' => array('cpp', 'cxx', 'cc'),
        'file_terminal' => array('bat', 'cmd', 'com'),
        'file_zip' => array('zip'),
        'file_configuration' => array('cfg', 'conf', 'config'),
        'file_window' => array('exe'),
        'file_vector' => array('ai', 'cdr', 'eps'),
        'file_bitmap' => array('bmp', 'bitmap', 'wbmp'),
        'file_image' => array('jpg', 'jpeg', 'gif', 'png', 'psd', 'tiff', 'tif', 'jpe'),
        'file_c' => array('c'),
        'file_coldfusion' => array('cfm'),
        'file_java' => array('java', 'class', 'jar'),
        'file_css' => array('css'),
        'file_javascript' => array('js'),
        'file_csharp' => array('cs'),
        'file_compressed' => array('ar', 'cbr', 'cbz', 'rar', 'tar', 'bz2', 'gz', 'lzma'),
        'file_linux' => array('sh'),
        'file_video' => array('wmv', 'mkv', 'avi', 'mpeg', 'mp4', 'mpg', 'flv', 'm4v', 'mov'),
        'file_sound' => array('mp3', 'wav', 'mid', 'wma'),
    );
    
    $icon = '';
    
    foreach($extension_uses as $icon_use => $extension_use) {
        if(in_array($extension, $extension_use)) {
            $icon = $icon_use;
            break;
        } 
    }
    
    if($url) {
        return "icon=" . $icon;
    } else {
        return $icon;
    }
}

